mapper.select:
select board_id, board_title, user_id, board_date, board_content
  from board 
 where board_id = #{boardId}

mapper.update:
update board
   set board_title 	= #{boardTitle}
   	  ,user_id		= #{userId}
   	  ,board_date		= #{boardDate}
   	  ,board_content	= #{boardContent}
 where board_id 	= #{boardId}
