mapper.select:
		select title
  		  from mytrip_info
 		 where mytrip_id = #{mytripId} and is_owner = 1;

select mytrip_id, title
  from mytrip_info
 where user_id = #{userId}

mapper.update:
		update mytrip_info
 		   set title = #{title}
 		 where mytrip_id = #{mytripId};

mapper.select:
select is_owner
  from mytrip_info
 where mytrip_id = #{mytripId} and user_id = #{userId}
